package ysoserial.payloads.util;  

import java.io.*;  

public class CmdUtils &#123;  

    public static String readStringFromInputStream(InputStream inputStream) throws Exception&#123;  
        StringBuilder stringBuilder = new StringBuilder("");  
        byte[] bytes = new byte[1024];  
        int n = 0;  
        while ((n=inputStream.read(bytes)) != -1)&#123;  
            stringBuilder.append(new String(bytes,0,n));  
        &#125;  
        return stringBuilder.toString();  
    &#125;  

    public static byte[] getBytes(String path) throws Exception&#123;  
        InputStream inputStream = new FileInputStream(path);  
        ByteArrayOutputStream byteArrayOutputStream = new ByteArrayOutputStream();  
        int n = 0;  
        while ((n=inputStream.read())!=-1)&#123;  
            byteArrayOutputStream.write(n);  
        &#125;  
        byte[] bytes = byteArrayOutputStream.toByteArray();  
        return bytes;  
    &#125;  


    public static byte[] readClassByte(String filename) throws IOException&#123;  
        File f = new File(filename);  
        if (!f.exists()) &#123;  
            throw new FileNotFoundException(filename);  
        &#125;  
        ByteArrayOutputStream bos = new ByteArrayOutputStream((int) f.length());  
        BufferedInputStream in = null;  
        try &#123;  
            in = new BufferedInputStream(new FileInputStream(f));  
            int buf_size = 1024;  
            byte[] buffer = new byte[buf_size];  
            int len = 0;  
            while (-1 != (len = in.read(buffer, 0, buf_size))) &#123;  
                bos.write(buffer, 0, len);  
            &#125;  
            return bos.toByteArray();  
        &#125; catch (IOException e) &#123;  
            e.printStackTrace();  
            throw e;  
        &#125; finally &#123;  
            try &#123;  
                in.close();  
            &#125; catch (IOException e) &#123;  
                e.printStackTrace();  
            &#125;  
            bos.close();  
        &#125;  
    &#125;  

    public static String getCodeFile(String codefile) &#123;  
        try&#123;  
            File file = new File(codefile);  
            if (file.exists())&#123;  
                FileReader reader = new FileReader(file);  
                BufferedReader bufferedReader = new BufferedReader(reader);  
                StringBuilder sb = new StringBuilder("");  
                String line = "";  
                while ((line=bufferedReader.readLine()) != null)&#123;  
                    sb.append(line);  
                    sb.append("\r\n");  
                &#125;  
                return sb.toString();  
            &#125; else &#123;  
                System.err.println(String.format("[-] %s is not exists!",codefile));  
                System.exit(0);  
                return null;  
            &#125;  
        &#125; catch (Exception e)&#123;  
            e.printStackTrace();  
            return null;  
        &#125;  
    &#125;  
